mapper.insert:
insert into class (
     class_id
     ,class_name
     ,class_start_date
     ,class_end_date
     ,take_charge_tea
     ,class_subject
) values( 
(select (nvl(max(class_id),0)+1) from class)
   ,#{class_name}
   ,to_date(#{class_start_date},'yyyy-mm-dd')
   ,to_date(#{class_end_date},'yyyy-mm-dd')
   ,#{take_charge_tea}
   ,#{class_subject}
 )

mapper.update:
 update
    class
 set
       class_name = #{class_name}
       , class_start_date = #{class_start_date}
       , class_end_date = #{class_end_date}
       , take_charge_tea = #{take_charge_tea}
       , class_subject = #{class_subject}
where
   class_id = '${class_id}'

update
   class_join_stu
set
   stu_id = #{stu_id}
where 
   class_id = #{class_id}

mapper.select:
select
CLASS_ID "CLASS_ID" , CLASS_NAME "class_name" ,(to_char(class_start_date,'yyyy-mm-dd')) "class_start_date" ,(to_char(class_end_date,'yyyy-mm-dd')) "class_end_date" ,take_charge_tea "take_charge_tea" ,CLASS_SUBJECT "class_subject"
from class c where class_id = ${class_id}

select stu_id "stu_id" ,stu_name "stu_name" ,decode( substr(jumin_num,7,1) ,'1', '남', '2', '여', '3', '남', '4', '여') "gender" ,to_number(to_char(sysdate,'yyyy'))- to_number(case substr(jumin_num,7,1) when '1' then '19' when '2' then '19' else '20' end||substr(jumin_num,1,2)) +1||'세' "age" ,phone_num "phone_num" ,to_char(regist_date,'yyyy-mm-dd') "regist_date" ,etc "etc"
,(SELECT CASE WHEN COUNT(*) > 0 THEN 'true' ELSE 'false' END FROM class_join_stu cj WHERE s.stu_id = cj.stu_id AND (class_id IS NULL OR class_id = ${class_id}) ) "isJoined"
from student s where 1=1 and stu_id in (select stu_id from class_join_stu where class_id = ${class_id})
