mapper.update:
update dayoff
	set
		DAYOFF_START =to_date(#{dayoff_start},'YYYY-MM-DD"T"HH24:MI')
		,DAYOFF_END	 =to_date(#{dayoff_end},'YYYY-MM-DD"T"HH24:MI')
		,DAYOFF_KIND =#{dayoff_kind}
		,ETC 		 =#{etc} 		
	where 
		day_id=#{day_id}

update dayoff
	set
		PAYMENT_STATUS = #{payment_status}				
	where 
		day_id=#{day_id}
