mapper.select:
select EXAM_ID "exam_id" ,EXAM_NAME "exam_name" ,to_char(EXAM_DATE,'yyyy-mm-dd') "exam_date"
,case when to_char(exam_date,'yyyy-mm-dd') < to_char(sysdate,'yyyy-mm-dd') then '응시 종료' when to_char(exam_date,'yyyy-mm-dd') = to_char(sysdate,'yyyy-mm-dd') then '응시 중' when to_char(exam_date,'yyyy-mm-dd') > to_char(sysdate,'yyyy-mm-dd') then '응시 전' end "is_end"
,(select class_name from class cla where cla.class_id = e.class_id) "class_name" from EXAM e where tea_id=#{tea_id}
