mapper.update:
update tea_dayoff
	set
		DAYOFF_START 	 = to_date(#{tea_dayoff_start},'YYYY-MM-DD"T"HH24:MI')
		,DAYOFF_END	 	 = to_date(#{tea_dayoff_end},'YYYY-MM-DD"T"HH24:MI')
		,TEA_DAYOFF_KIND = #{tea_dayoff_kind}
		,ETC 		 	 = #{tea_etc} 		
	where 
		day_id =#{tea_day_id}

update tea_dayoff
	set
		PAYMENT_STATUS = #{tea_payment_status}					
	where 
		day_id=#{tea_day_id}
